import React from 'react';
import { Row, Col } from 'react-bootstrap';
import Product from '../components/Product';
import {useEffect, useState} from 'react';
import axios from 'axios';

const HomeScreen = () => {
    const [products, setProducts] = useState ([]);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await axios.get('/api/products'); // Uses proxy
                console.log('✅ API Response:', response);
                console.log('✅ API Data:', response.data);
                setProducts(response.data);
            } catch (error) {
                console.error('❌ API Error:', error.response ? error.response.data : error.message);
            }
        };

        fetchProducts();
    }, []);



return (
    <>
        <h1 className="text-center my-3">Latest Products</h1>
        {products.length === 0 ? (
            <h2 className="text-center text-danger">No products found</h2>
        ) : (
            <Row>
                {products.map((product) => (
                    <Col key={product._id} sm={12} md={6} lg={4} xl={3} className="d-flex justify-content-center">
                    <h3>{product.name}</h3> {/* Debug: Show product name */}
                    <Product product={product} />
                    </Col>
                ))}
            </Row>
        )}
    </>
);
};
export default HomeScreen;
