import React from 'react';
import { Row, Col } from 'react-bootstrap';
import products from "../products";
import Product from '../components/Product';

const HomeScreen = () => {
  console.log("Products array:", products); // ✅ Debugging output

  if (!products || products.length === 0) {
    return <p>No products available.</p>;  // ✅ Prevents crash if `products` is empty
  }

  return (
    <>
      <h1>Latest Products</h1>
      <Row>
        {products.map((product, index) => {
          console.log("Rendering product:", product);  // ✅ Debugging each product

          return product ? (
            <Col key={index} sm={12} md={6} lg={4} xl={3}>
              <Product product={product} productKey={index} /> {/* Pass index */}
            </Col>
          ) : null;
        })}
      </Row>
    </>
  );
};

export default HomeScreen;
