import React from 'react';
import { Navbar, Nav, Container, NavDropdown } from 'react-bootstrap';
import { FaShoppingCart, FaUser } from 'react-icons/fa';
import { Link } from 'react-router-dom'; // ✅ Using Link instead of LinkContainer
import './Header.css';

const Header = () => {
  return (
    <header>
      <Navbar bg="dark" variant="dark" expand="lg" className="custom-navbar" collapseOnSelect>
        <Container>
          <Navbar.Brand as={Link} to="/" className="brand">
            <img src="/logo.png" alt="GateKeep Logo" className="logo" />
            <span>GATEKEEP</span>
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="ms-auto">
              <Nav.Link as={Link} to="/cart" className="nav-link-custom">
                <FaShoppingCart className="me-2" /> Cart
              </Nav.Link>

              {/* ✅ User Dropdown Menu */}
              <NavDropdown title={<><FaUser className="me-2" /> Account</>} id="username">
              </NavDropdown>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    </header>
  );
};

export default Header;
