import React from 'react';
import { Card } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import Rating from './Rating';

const Product = ({ product, productKey }) => {
  console.log("Product received in Product.jsx:", product);  // ✅ Debugging

  if (!product) {
    console.log("⚠️ WARNING: Product is undefined:", product);
    return <p>Product data not available.</p>;  // ✅ Prevents crash if product is undefined
  }

  return (
    <Card className="my-3 p-3 rounded">
      <Link to={`/product/${productKey}`}> {/* ✅ Use index instead of _id */}
        <Card.Img src={product.image} variant="top" />
      </Link>

      <Card.Body>
        <Link to={`/product/${productKey}`}> {/* ✅ Use index instead of _id */}
          <Card.Title as="div">
            <strong>{product.name}</strong>
          </Card.Title>
        </Link>

        <Card.Text as="div">
          <Rating value={product.rating} text={`${product.numReviews} reviews`} />
        </Card.Text>

        <Card.Text as="h3">${product.price}</Card.Text>
      </Card.Body>
    </Card>
  );
};

export default Product;
